
public class Tabelle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String a = "-20";
		String b = "-10";
		String c = "0";
		String d = "+20";
		String e = "+30";
		
		String aa = "-28.89";
		String bb = "-23.33";
		String cc = "-17.78";
		String dd = "-6.67";
		String ee = "-1.11";
		
		String f = "Fahrenheit";
		String g = "Celsius";
		
		System.out.printf("%-12s", f);
		System.out.print("|");
		System.out.printf("%10s\n", g);
		System.out.println("-----------------------");
		System.out.printf("%-12s", a);
		System.out.print("|");
		System.out.printf("%10s\n", aa);
		System.out.printf("%-12s", b);
		System.out.print("|");
		System.out.printf("%10s\n", bb);
		System.out.printf("%-12s", c);
		System.out.print("|");
		System.out.printf("%10s\n", cc);
		System.out.printf("%-12s", d);
		System.out.print("|");
		System.out.printf("%10s\n", dd);
		System.out.printf("%-12s", e);
		System.out.print("|");
		System.out.printf("%10s\n", ee);
		
		
		
		int a2 = -20;
		int b2 = -10;
		int c2 = 0;
		int d2 = +20;
		int e2 = +30;
		
		double aa2 = -28.8889;
		double bb2 = -23.3333;
		double cc2 = -17.7778;
		double dd2 = -6.6667;
		double ee2 = -1.1111;
		
		System.out.printf("\n\n\n%-12s", f);
		System.out.print("|");
		System.out.printf("%10s\n", g);
		System.out.printf("%.23s", "-----------------------.-----");
		System.out.printf("\n%-12d", a2);
		System.out.print("|");
		System.out.printf("%10.2f\n", aa2);
		System.out.printf("%-12d", b2);
		System.out.print("|");
		System.out.printf("%10.2f\n", bb2);
		System.out.printf("%-12d", c2);
		System.out.print("|");
		System.out.printf("%10.2f\n", cc2);
		System.out.printf("%-+12d", d2);
		System.out.print("|");
		System.out.printf("%10.2f\n", dd2);
		System.out.printf("%-+12d", e2);
		System.out.print("|");
		System.out.printf("%10.2f\n", ee2);
		
		
		
		
		
	}

}
